import s from "./modal.module.css"
import { CloseIcon } from "@ya.praktikum/react-developer-burger-ui-components"
import { useEffect } from "react"

export default function Modal({content, openPopup}){

    useEffect(() => {
        //Устанавливаем закрытие по нажатию на esc
        const close = (e) => {e.keyCode === 27 
                             ? openPopup(false) 
                             : console.log("для закрытия нажмите esc")}

        document.addEventListener('keydown', close)
        return () => document.removeEventListener('keydown', close)
    },[])

    //Устанавливаем закрытие по нажатию на тёмный фон. 
    //Я решил не прокидвать портал и наржуать всё, а сделать просто сверку с тем section это или нет
    const closeToClickBackgound = (e) => {console.log(e.target == <section></section>)}

    //{content.popupContent.title}
    return(<section className={s.modal} onClick={closeToClickBackgound}>
        <div className={s.title}>
            <h3 className="text text_type_main-large">{content.title}</h3>
            <CloseIcon type="primary" onClick={() => openPopup(false)} />
        </div>
        {content.content}
    </section>)
}
