import s from "./burger-constructor.module.css"
import { ConstructorElement, Button } from "@ya.praktikum/react-developer-burger-ui-components"


export default function BurgerConstructor(ingredients){
    {/*Ниже по соответствующим массивам распределяем игредиенты*/}
    const [main, bun, sauce] = [[],[],[]]
    ingredients.ingreds.forEach(ingred => {
        ingred.type === 'bun'   ? bun.push(ingred)  :
        ingred.type === 'main'  ? main.push(ingred) :
        ingred.type === 'sauce' ? sauce.push(ingred): console.log("Нужный тип не найден")
    })


    return( <section className={s.brg_construct}>
                <div className={s.constructor}>
                    {ingredients.ingreds.map(ingred => {return(
                        <ConstructorElement
                        //type="top"
                        //type="bottom"
                        isLocked={true}
                        text={ingred.name}
                        price={ingred.price}
                        thumbnail={ingred.image}
                        />
                    )})}
                </div>
                <div className={s.info}>
                    <p className="text text_type_digits-medium">100</p>
                </div>
                <Button htmlType="button" type="primary" size="large">
                    Оформить заказ
                </Button>
            </section>
    )
}
